(()=>{"use strict";!function(){document.querySelector(".showData");const t=document.querySelector(".searchBtn"),e=document.getElementById("searchArea"),n=document.querySelector("#searchArea + span.error"),o=document.getElementById("cityAndCountry"),c=document.getElementById("date"),a=document.getElementById("time"),r=document.getElementById("week");t.addEventListener("click",(t=>{t.preventDefault(),""===e.value?n.textContent="Please enter a location":async function(t){n.textContent="";const e=`http://api.weatherapi.com/v1/current.json?key=b5e7182569e7426eb6e160345230310&q=${t}&aqi=yes`;try{const t=await fetch(e,{mode:"cors"});t.ok||(n.textContent="Location not found!");const i=await t.json();if(i.location.name&&i.location.country){const t=String(i.location.name),e=String(i.location.country);o.textContent=`${t}, ${e}`}if(i.location.localtime){const t=i.location.localtime,e=new Date(t),n=e.getFullYear(),o=e.getMonth()+1,u=e.getDate(),l=e.getHours(),s=e.getMinutes(),d=l>=12?"PM":"AM",m=["Sun","Mon","Tue","Wed","Thus","Fri","Sat"][e.getDay()];c.textContent=`${u}/${o}/${n}`,a.textContent=s<10?`${l%12||12}:0${s} ${d}`:`${l%12||12}:${s} ${d}`,r.textContent=`${m}`}}catch(t){console.error(t)}}(String(e.value))}))}()})();